import styled from "styled-components";

export const MainLayoutContainer = styled.div`
  width: 100%;
  height: 100vh;
  min-height: 600px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
`;

export const MainLayoutContainer2 = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  text-align: center;

  h1 {
    position: absolute;
    z-index: 20;
  }
`;
